<?php

namespace Cleantalk\ApbctWP;

if( ! class_exists( '\CleantalkSP\SpbctWP\AdminBar' ) || ! class_exists( '\Cleantalk\ApbctWP\AdminBar' ) ) {

	class AdminBar {

		public static function showAdminBar( $wp_admin_bar ) {
			//@ToDo implement some common layout
			$args = array(
				'id'	=> 'cleantalk_common_parent_node',
				'title' => '<img src="data:image/png;base64," alt=""  height="" style="margin-top:9px; float: left;" />');
			$wp_admin_bar->add_node( $args );
			return apply_filters( 'cleantalk_admin_bar', $wp_admin_bar );
		}

	}

}
